  async scan(): Promise<ScanResult> {
    const logger = getLogger();
    const startTime = Date.now();
    const issues: Issue[] = [];

    logger.info('Running gap analysis...');

    // Run all checks in parallel
    const [
      todoIssues,
      missingTestIssues,
      incompleteIssues,
      serviceViolationIssues,
